CREATE TABLE CASEEXAMPLE (ID BIGINT NOT NULL, FOOBAR VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE enum_example (id BIGINT AUTO_INCREMENT NOT NULL, as_int INTEGER NOT NULL, as_string VARCHAR(255), PRIMARY KEY (id))
